ShrubberyCreationForm::ShrubberryCreationForm(const std::string &target) : AForm("ShrubberyCreationForm", 145, 137), target(target) {}

ShrubberyCreationForm::ShrubberryCreationForm(const ShrubberyCreationForm& other) : AForm(other), target(other.target) {}

ShrubberyCreationForm& ShrubberyCreationForm::operator=(const ShrubberyCreationForm& other) {
	if (this != &other) {
		AForm::operator=(other);
		target = other.target;
	}
	return (*this);
}

ShrubberyCreationForm::~ShrubberryCreationForm() {}

void ShrubberyCreationForm::execute(const Bureaucrat& bureaucrat) const {
	checkExecutionRequirements(bureaucrat);

	std::ofstream outputFile((getTarget() + "_shrubbery").c_str());
	if (!outputFile)
		throw std::runtime_error("Couldn;t open file for writing");
	outputFile << tree;
	outputFile.close();
}